import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './Login.css';
import { signInWithEmailAndPassword } from 'firebase/auth';
import { auth } from '../firebase/firebase';

export const Login = ({ setLoginSiginin }) => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [errorMessage, setErrorMessage] = useState('');
  const navigate = useNavigate();

  const handleSubmit = async (event) => {
    event.preventDefault();
    if (email === '' || password === '') {
      setErrorMessage('Please fill in all fields.');
      return;
    } else if (password.length < 8) {
      setErrorMessage('Password must be at least 8 characters long.');
      return;
    }

    try {
      await signInWithEmailAndPassword(auth, email, password);
      setErrorMessage('Login successful!');
      setLoginSiginin(false);
      navigate('/');  // Redirect to home page
    } catch (error) {
      setErrorMessage('Invalid email or password.');
    }
  };
  const closeModal = () => {
    console.log("close login");
    setLoginVisible(false); 
    /*
    const inputs = document.querySelectorAll(".Login-input");
    inputs.forEach(input => {
        input.value = "";
    });
    */
  };

  return (
  <div className="login-container">
    <button className="close-button" onClick={() => { /* Add close functionality */ }}>âœ•</button>
    <h2>Login</h2>
    {errorMessage && <p className="error-message">{errorMessage}</p>}
  
    <form onSubmit={handleSubmit}>
      <div>
        <label>Email</label>
        <input 
          type="email" 
          className="Login-input"  // Apply class for styling input
          value={email} 
          onChange={(e) => setEmail(e.target.value)} 
        />
      </div>
      <div>
        <label>Password</label>
        <input 
          type="password" 
          className="Login-input"  // Apply class for styling input
          value={password} 
          onChange={(e) => setPassword(e.target.value)} 
        />
      </div>
      
      <button type="submit" className="login-container-Login-button">
        Login
      </button>
    </form>
  </div>
  
  );
};
