import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './Signup.css';
import { createUserWithEmailAndPassword } from 'firebase/auth';
import { auth } from '../firebase/firebase';

export const Signup = ({ setLoginSiginin }) => {
  const [username, setUsername] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [errorMessage, setErrorMessage] = useState('');
  const[isSuccess,setisSuccess]=useState(false)
  const navigate = useNavigate();

  const handleSubmit = async (event) => {
    event.preventDefault();
    //validate fieldes
    if (username === '' || email === '' || password === '') {
      setErrorMessage('Please fill in all fields.');
      return;
    } else if (password.length < 8) {
      setErrorMessage('Password must be at least 8 characters long.');
      return;
    }

    try {
      await createUserWithEmailAndPassword(auth, email, password);
      console.log(Response);
      setErrorMessage('Signup successful!');
      setLoginSiginin(false);
      setLoginSiginin(false);
      navigate('/');
    } catch (error) {
      if((error.message.includes('aut')))
      setErrorMessage(error.message.includes('auth/email-already-in-use') ? 'Email already in use.' : 'An error occurred during signup.');
    }
  };

  return (
  <div className="signup-container">
    <button className="close-button" onClick={() => { /* Add close functionality */ }}>âœ•</button> 
      <h2>Signup</h2>
          {errorMessage && <p className="error-message">{errorMessage}</p>}
          
          <form onSubmit={handleSubmit}>
            <div>
              <label>Username</label>
              <input 
                type="text" 
                className="Signup-input"  // Apply class from CSS
                value={username} 
                onChange={(e) => setUsername(e.target.value)} 
              />
            </div>
            
            <div>
              <label>Email</label>
              <input 
                type="email" 
                className="Signup-input"  // Apply class from CSS
                value={email} 
                onChange={(e) => setEmail(e.target.value)} 
              />
            </div>
            
            <div>
              <label>Password</label>
              <input 
                type="password" 
                className="Signup-input"  // Apply class from CSS
                value={password} 
                onChange={(e) => setPassword(e.target.value)} 
              />
            </div>
            
            <button type="submit" className="signup-container-Signup-button">
              Signup
            </button>
        </form>
  </div>
  
  );
};
